t(u32 *u32Size);u32 IrUsartRxRdRtrGet(u32 *u32Ptr);u32 IrUsartRxRdRtrSet(u32 *u32Data);u32 IrUsartRxWtrRtrGet(u32 *u32Data);#endif //__UART_H__